import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
  // const [enterTitle, setEntertitle] = useState('');
  // const [enterAmount, setEnterAmount] = useState('');
  // const [enterDate, setEnterDate] = useState('');

  const [userInput, setUserInPut] = useState({
    enterTitle: '',
    enterAmount: '',
    enterDate: '',
  });

  const titleChangeHandaler = (event) => {
    //setEntertitle(event.target.value);
    setUserInPut({
      ...userInput,
      enterTitle: event.target.value,
    });
  };

  const AmountChangeHandaler = (event) => {
    //etEnterAmount(event.target.value);
     setUserInPut({
      ...userInput,
      enterTitle: event.target.value,
    });
  };

  const DateChangeHandaler = (event) => {
    //setEnterDate(event.target.value);
     setUserInPut({
      ...userInput,
      enterTitle: event.target.value,
    });
  };

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandaler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            onChange={AmountChangeHandaler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2019-01-01"
            max="2022-12-31"
            onChange={DateChangeHandaler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit"> Add Expense </button>
      </div>
    </form>
  );
};

export default ExpenseForm;
